tableSchema.table:
CREATE TABLE IF NOT EXISTS `LOGIN_ERROR_LOG` (
`ID` INT(11) UNSIGNED NOT NULL AUTO_INCREMENT,
`USER_ID` INT(11) UNSIGNED NOT NULL,
`ERROR_TIME` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP(),
PRIMARY KEY (`ID`)
);

CREATE TABLE IF NOT EXISTS `VALIDATE_CODE_INFO` (
`ID` INT(11) UNSIGNED NOT NULL AUTO_INCREMENT,
`USER_ID` INT(11) UNSIGNED NOT NULL,
`VALIDATE_CODE` VARCHAR(64) NOT NULL UNIQUE,
PRIMARY KEY (`ID`)
);
